mapper.select:
select h.hno, h.htitle, h.hcontent, h.hdate, h.hcount, h.haddr, h.hphoto, h.hlat, h.hlng
from hotplace h
where h.hno = #{value}

mapper.update:
update discount set 
  hcount = hcount + 1 
where hno = #{value}

update hotplace
htitle=#{title}, hcontent=#{content}, hphoto=#{photo}, haddr=#{address}, hlat=#{latitude}, hlng=#{longitude}
where hno=#{no}
